"use client";
import React from 'react';
import Image from 'next/image';
import { motion } from 'framer-motion';

export default function LandingPage() {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3,
        delayChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const imageVariants = {
    hidden: { scale: 0.8, opacity: 0 },
    visible: {
      scale: 1,
      opacity: 1,
      transition: {
        duration: 0.6,
        ease: "backOut"
      }
    },
    hover: {
      scale: 1.05,
      transition: { duration: 0.3 }
    }
  };

  return (
    <motion.div
      className="min-h-full flex items-center justify-between px-6 md:px-12 lg:px-24 py-20 bg-gradient-to-br from-gray-900 via-purple-900 to-gray-800"
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      <motion.div
        className="w-full md:w-1/2 flex justify-center"
        variants={imageVariants}
        whileHover="hover"
      >
        <Image
          src="/profile-pic3.png"
          alt="Landing Page"
          width={400}
          height={400}
          className="rounded-full shadow-2xl object-cover border-4 border-purple-500/20 hover:border-purple-500/50 transition-all duration-500"
        />
      </motion.div>

      <motion.div className="w-full md:w-1/2 space-y-6">
        <motion.h1
          className="text-4xl md:text-5xl lg:text-6xl font-bold text-gray-100 leading-tight"
          variants={itemVariants}
        >
          Transformo ideias em interfaces funcionais e elegantes.
        </motion.h1>

        <motion.p
          className="text-xl text-purple-300"
          variants={itemVariants}
        >
          Design • Desenvolvimento • Experiência do Usuário
        </motion.p>

        <motion.div variants={itemVariants}>
          <motion.button
            className="mt-8 px-8 py-3 bg-purple-600 hover:bg-purple-700 text-white font-semibold rounded-full shadow-lg transition-all duration-300 hover:shadow-xl hover:scale-105"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Vamos criar algo incrível juntos
          </motion.button>
        </motion.div>
      </motion.div>
    </motion.div>
  );
}
