import { ProjectCardProps } from '@/types/types';
import { motion } from 'framer-motion';
import Image from 'next/image';
import Link from 'next/link';

export function ProjectCard({ project, index }: ProjectCardProps) {
    return (
        <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: index * 0.1 }}
            viewport={{ once: true, margin: "-50px" }}
            whileHover={{ y: -10 }}
            className="group relative overflow-hidden rounded-xl border border-gray-700 bg-gray-800/50 transition-all hover:border-purple-500/50"
        >
            <div className="relative h-48 overflow-hidden">
                <Image
                    src={project.image}
                    alt={project.title}
                    fill
                    className="object-cover transition-all duration-500 group-hover:scale-105"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-gray-900/90 via-gray-900/30 to-transparent" />
            </div>

            <div className="p-6">
                <h3 className="text-xl font-bold text-white mb-2">{project.title}</h3>
                <p className="text-gray-300 mb-4">{project.description}</p>

                <div className="flex flex-wrap gap-2 mb-4">
                    {project.tags.map((tag, i) => (
                        <span
                            key={i}
                            className="px-3 py-1 bg-gray-700/50 text-sm text-purple-300 rounded-full"
                        >
                            {tag}
                        </span>
                    ))}
                </div>

                {project.link && (
                    <Link
                        href={project.link}
                        target="_blank"
                        className="inline-flex items-center text-sm font-medium text-white hover:text-purple-300 transition-colors"
                    >
                        Ver projeto
                        <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7m0 0l-7 7m7-7H3" />
                        </svg>
                    </Link>
                )}
            </div>

            <div className="absolute inset-0 bg-gradient-to-br from-purple-500/10 to-pink-500/10 opacity-0 group-hover:opacity-100 transition-opacity" />
        </motion.div>
    );
}

import { ProjectCardProps } from '@/types/types';
import { motion } from 'framer-motion';
import Image from 'next/image';
import Link from 'next/link';

export function ProjectCard({ project, index }: ProjectCardProps) {
    return (
        <motion.div
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{
                duration: 0.5,
                delay: index * 0.1,
                ease: "easeOut"
            }}
            viewport={{ once: true, margin: "-50px" }}
            whileHover={{
                y: -8,
                transition: {
                    type: "tween",
                    ease: "easeOut",
                    duration: 0.3
                }
            }}
            className="group relative overflow-hidden rounded-xl border border-gray-700 bg-gray-800/50 hover:border-purple-500/50"
        >
            <motion.div
                className="relative h-48 overflow-hidden"
                whileHover={{
                    scale: 1.03,
                    transition: { duration: 0.4 }
                }}
            >
                <Image
                    src={project.image}
                    alt={project.title}
                    fill
                    className="object-cover"
                    style={{
                        transition: "transform 0.5s cubic-bezier(0.16, 1, 0.3, 1)",
                        transformOrigin: "center bottom"
                    }}
                />
                <div className="absolute inset-0 bg-gradient-to-t from-gray-900/90 via-gray-900/30 to-transparent" />
            </motion.div>

            <div className="p-6">
                <h3 className="text-xl font-bold text-white mb-2">{project.title}</h3>
                <p className="text-gray-300 mb-4">{project.description}</p>

                <div className="flex flex-wrap gap-2 mb-4">
                    {project.tags.map((tag, i) => (
                        <span
                            key={i}
                            className="px-3 py-1 bg-gray-700/50 text-sm text-purple-300 rounded-full"
                        >
                            {tag}
                        </span>
                    ))}
                </div>

                {project.link && (
                    <Link
                        href={project.link}
                        target="_blank"
                        className="inline-flex items-center text-sm font-medium text-white hover:text-purple-300 transition-colors duration-300"
                    >
                        Ver projeto
                        <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M14 5l7 7m0 0l-7 7m7-7H3" />
                        </svg>
                    </Link>
                )}
            </div>

            <motion.div
                className="absolute inset-0 bg-gradient-to-br from-purple-500/10 to-pink-500/10 opacity-0"
                whileHover={{
                    opacity: 1,
                    transition: { duration: 0.4 }
                }}
            />
        </motion.div>
    );
}

"use client";

import { ProjectCard } from "./ProjectCard";

export function PortfolioSection() {
    const projects = [
        {
            title: "Edunex",
            description: "Plataforma de cursos online",
            tags: ["Next.js", "Tailwind"],
            image: "/Edunex-preview.png"
        },
        {
            title: "Versa",
            description: "Loja online de roupas modernas e acessíveis",
            tags: ["React", "CSS", "Mantine"],
            image: "/versa-preview.png"
        },
        {
            title: "Enadex",
            description: "Descrição exemplo mudar depois",
            tags: ["React", "CSS", "Chakra UI"],
            image: "/Enadex-preview.png"
        },
    ];

    return (
        <section className="py-20">
            <h2 className="text-3xl font-bold text-center mb-16 text-white">
                Trabalhos <span className="text-purple-400">Destacados</span>
            </h2>

            <div className="container mx-auto grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                {projects.map((project, index) => (
                    <ProjectCard key={index} project={project} index={index} />
                ))}
            </div>
        </section>
    );
}
